#include <iostream>

class Soldier
{
public:
    std::string name;
    int life;
    int damage;

    Soldier(std::string name, int life, int damage) {

        this->name = name;
        this->life = life;
        this->damage = damage;
    }

    bool lives() {
        return life > 0;
    }

    friend std::ostream &operator<< (std::ostream &stream, Soldier &soldier) {
        stream << soldier.name << " " << soldier.life << " " << soldier.damage;
        return stream;
    }

};


Soldier &fight(Soldier &first, Soldier &second)
{
    for (; first.lives() && second.lives() ;) {
        first.life = first.life - second.damage;
        second.life = second.life - first.damage;
    }

    if (first.life > second.life)
        return first;
    else
        return second;
}


int main()
{
    Soldier me {"Nandi", 100, 10};
    Soldier enemy {"Barbarian", 80, 5};

    std::cout << fight(me, enemy) << std::endl;

    return 0;
}

listitem.para:
                                Matyi fejben kiszámolta, még a futtatás előtt, hogy 60-at fog kiírni. Módosítsuk úgy a programot a
                                harcol függvényben, hogy
                                a harc minden lépésében nyomja ki a két katonát a képernyőre, azaz az alábbi futási eredmény legyen:

